   input: [{0, 3}, {1, 1}, {2, 2}, {4, 4}, {0, 0}, {1, 2}, {3, 1}, {3, 3}]
   output: 13.4477
*/
double convexHullPerimeter(std::vector<Point> const& points) {

outputs:
	if (points.size() == 0) return 0.0;

	// Find the point with the lowest x-coordinate (and lowest y-coordinate in case of ties)
	auto cmp = [](Point const& p1, Point const& p2) {
		return p1.x < p2.x || (p1.x == p2.x && p1.y < p2.y);
	};
	Point pivot = *std::min_element(points.begin(), points.end(), cmp);

	// Sort the points based on the polar angle with respect to the pivot
	auto angleCmp = [pivot](Point const& p1, Point const& p2) {
		double dx1 = p1.x - pivot.x, dy1 = p1.y - pivot.y;
		double dx2 = p2.x - pivot.x, dy2 = p2.y - pivot.y;
		double cross = dx1 * dy2 - dx2 * dy1;
		if (cross != 0) return cross > 0;
		return std::sqrt(dx1*dx1 + dy1*dy1) < std::sqrt(dx2*dx2 + dy2*dy2);
	};
	std::vector<Point> sortedPoints = points;
	std::sort(sortedPoints.begin(), sortedPoints.end(), angleCmp);

	// Build the convex hull using Graham's scan
	std::vector<Point> hull;
	for (Point const& p : sortedPoints) {
		while (hull.size() >= 2) {
			Point const& p2 = hull.back();
			Point const& p1 = hull[hull.size() - 2];
			double cross = (p2.x - p1.x)*(p.y - p1.y) - (p.x - p1.x)*(p2.y - p1.y);
			if (cross <= 0) break;
			hull.pop_back();
		}
		hull.push_back(p);
	}

	// Compute the perimeter of the convex hull
	double perimeter = 0.0;
	for (size_t i = 0; i < hull.size(); ++i) {
		perimeter += distance(hull[i], hull[(i + 1) % hull.size()]);
	}
	return perimeter;
}

