    for (size_t i = 0; i < N; ++i) {
        if (!visited[i]) {
            ++components;
            std::stack<size_t> stack;
            stack.push(i);
            visited[i] = true;

            while (!stack.empty()) {
                size_t node = stack.top();
                stack.pop();

                for (size_t neighbor = 0; neighbor < N; ++neighbor) {
                    if (A[node * N + neighbor] && !visited[neighbor]) {
                        visited[neighbor] = true;
                        stack.push(neighbor);
                    }
                }
            }
        }
    }

    return components;
}
