void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
    // Create a dense NxN matrix from the sparse COO representation
    std::vector<std::vector<double>> matrix(N, std::vector<double>(N, 0.0));
    for (const auto& elem : A) {
        matrix[elem.row][elem.column] += elem.value;
    }

    // Perform Gaussian elimination
    for (size_t i = 0; i < N; ++i) {
        // Find the pivot row (the current row with the maximum value in column i)
        size_t pivot_row = i;
        double max_val = std::abs(matrix[i][i]);
        for (size_t j = i + 1; j < N; ++j) {
            double current_val = std::abs(matrix[j][i]);
            if (current_val > max_val) {
                pivot_row = j;
                max_val = current_val;
            }
        }

        // Swap the pivot row with the current row
        std::swap(matrix[i], matrix[pivot_row]);
        std::swap(const_cast<std::vector<double>&>(b)[i], const_cast<std::vector<double>&>(b)[pivot_row]);

        // Normalize the pivot row
        double pivot_val = matrix[i][i];
        if (std::abs(pivot_val) < 1e-10) {
            throw std::runtime_error("Matrix is singular or nearly singular");
        }
        for (size_t j = i; j < N; ++j) {
            matrix[i][j] /= pivot_val;
        }
        const_cast<std::vector<double>&>(b)[i] /= pivot_val;

        // Eliminate the current column in other rows
        for (size_t j = 0; j < N; ++j) {
            if (j != i && std::abs(matrix[j][i]) > 1e-10) {
                double factor = matrix[j][i];
                for (size_t k = i; k < N; ++k) {
                    matrix[j][k] -= factor * matrix[i][k];
                }
                const_cast<std::vector<double>&>(b)[j] -= factor * const_cast<std::vector<double>&>(b)[i];
            }
        }
    }

    // Extract the solution vector
    x = b;
}

